import React from 'react';
import { Block } from './Block';
import './currencyConvertor.scss';

function App() {
  const [formCurrency, setFromCurrency] = React.useState('RUB');
  const [toCurrency, setToCurrency] = React.useState('USD');
  const ratesRef = React.useRef({});
  const [formPrice, setFromPrice] = React.useState(0);
  const [toPrice, setToPrice] = React.useState(1);
  React.useEffect(() => {
    fetch('https://cdn.cur.su/api/latest.json')
      .then((res) => res.json())
      .then((json) => {
        setRates(json.rates);
onChangeToPrice(1)
      })
      .catch((err) => {
        console.warn(err);
        alert('error');
      });
  }, []);
  const onChangeFromPrice = (value) => {
    const price = value / rates[formCurrency];
    const result = price * rates[toCurrency];
    setToPrice(result);
    setFromPrice(value);
  };
  const onChangeToPrice = (value) => {
    const result = (rates[formCurrency] / rates[toCurrency]) * value;
    setFromPrice(result);
    setToPrice(value);
  };
  React.useEffect(() => {
    onChangeFromPrice(formPrice);
  }, [formCurrency]);
  React.useEffect(() => {
    onChangeToPrice(toPrice);
  }, [toCurrency]);
  return (
    <div className="body_currencyConvertor">
      <div className="currencyConvertor">
        <Block
          value={formPrice}
          currency={formCurrency}
          onChangeValue={onChangeFromPrice}
          onChangeCurrency={setFromCurrency}
        />
        <Block
          value={toPrice}
          currency={toCurrency}
          onChangeValue={onChangeToPrice}
          onChangeCurrency={setToCurrency}
        />
      </div>
    </div>
  );
}

export default App;
