import React from 'react';
import './photos.scss';
import Collection from './components/Collection';
const  categories = [
    { "name": "Все" },
    { "name": "Море" },
    { "name": "Горы" },
    { "name": "Архитектура" },
    { "name": "Города" }
  ]
function App() {
  const [collection, setCollection] = React.useState([]);
  const [searchValue, setSearchValue] = React.useState('');
  const [category, setCategory] = React.useState(0);
  React.useEffect(() => {
    fetch('https://62ea7a82ad295463258d86d1.mockapi.io/city')
      .then((res) => res.json())
      .then((json) => {
        setCollection(json);
      })
      .catch((err) => {
        console.warn(err);
        alert('error ');
      });
  }, []);
  const onChangeSearchValue = (event) => {
    setSearchValue(event.target.value);
  };
  return (
    <div className="body_photos">
      <div className="photos">
        <h1>Моя коллекция фотографий</h1>
        <div className="top">
          <ul className="tags">
                 {categories.map((obj) => <li className='active' key={obj.name}>{obj.name}</li>)}
          </ul>
          <input
            value={searchValue}
            onChange={onChangeSearchValue}
            className="search-input"
            placeholder="Поиск по названию"
          />
        </div>
        <div className="content_photos">
          {collection
            .filter((obj) => {
              return obj.name.toLowerCase().includes(searchValue.toLocaleLowerCase());
            })
            .map((obj, index) => (
              <Collection key={index} name={obj.name} images={obj.photos} />
            ))}
        </div>
        <ul className="pagination">
          <li>1</li>
          <li className="active">2</li>
          <li>3</li>
        </ul>
      </div>
    </div>
  );
}

export default App;
