import React from 'react';
import { Block } from './Block';
import './currencyConvertor.scss';

function App() {
    const [formCurrency, setFromCurrency] = React.useState('RUB');
    const [toCurrency, setToCurrency] = React.useState('USD');
    const [rates, setRates] = React.useState({});
    const [formPrice, setFromPrice] = React.useState(0);
    const [toPrice, setToPrice] = React.useState(0);
    React.useEffect(()=> {
        fetch('https://cdn.cur.su/api/latest.json').then((res)=>res.json()).then((json)=> {
            setRates(json.rates);
            console.log(json.rates)
        }).catch((err)=>{
            console.warn(err)
            alert('error')
        })
    }, [])
    const onChangeFromPrice = (value) => {
setFromPrice(value)
    }
  return (
    <div className='body_currencyConvertor'>
    <div className="currencyConvertor">
      <Block value={0} currency={formCurrency} onChangeCurrency={setFromCurrency} />
      <Block value={0} currency={toCurrency} onChangeCurrency={setToCurrency} />
    </div>
    </div>
  );
}

export default App;
